import React, {
  Component
} from 'react';

import { 
  Card,
  Form,
  Input,
  Label, 
  Button, 
  CardTitle, 
  CardText } from 'reactstrap';

import {
  Map,
  TileLayer,
  Marker,
  Popup
} from 'react-leaflet';

import L from 'leaflet';

import './App.css';

var myIcon = L.icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [25, 41],
  iconAnchor: [12.5, 41],
  popupAnchor: [0, -41]
});

class App extends Component {
  state = {
    location: { // set default map position is in london
      lat: 51.505,
      lng: -0.09,
    },
    haveUsersLocation: false,
    zoom: 2, // start map zoomed very far out
  }

  // here we ask the user's permission to retrieve their location
  // we then save their lat + lng into our state 
  componentDidMount() { 
    navigator.geolocation.getCurrentPosition((position) => {
      this.setState({
        location: {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        },
        haveUsersLocation: true,
        zoom: 13
      });
    }, 
      // if the user denies us permission to access their location
      // then, fall back to finding the users location via IP address
      () => {
      console.log('uh oh.. no location permissions were given');
      fetch('https://ipapi.co/json')
        .then(res => res.json())
        .then(location => {
          console.log(location);
          this.setState({
            location: {
              lat: location.latitude,
              lng: location.longitude
            }
          })
        })
    });
  }


  render() {
    const position = [this.state.location.lat, this.state.location.lng];

    return (
    <div className="map">
      <Map className="map" center = { position } zoom = { this.state.zoom }>
        <TileLayer url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution = "&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors" 
        />
        { 
          this.state.haveUsersLocation ? 
          <Marker 
            position = { position }
            icon = { myIcon } >
            <Popup>
              This is your location! <br/> 
            </Popup> 
          </Marker> : ''
        }   
      </Map>
      <Card body className="message-form">
        <CardTitle>Welcome to Guest Map</CardTitle>
        <CardText>Leave a message with your location</CardText>
        <CardText>Thanks for stopping by!</CardText>
      </Card>
      <Form className="message-form-email">
        <Label for="exampleEmail">Email</Label>
        <Input type="email" name="email" id="exampleEmail" placeholder="Enter Your Email Here"></Input>
      </Form>
    </div>
    );
  }
}

export default App;
